import { typeColors, statusColors } from '@workday/canvas-colors-web';
export var variants = {
    error: {
        color: statusColors.error,
    },
    hint: {
        color: typeColors.hint,
    },
    inverse: {
        color: typeColors.inverse,
    },
};
