import { useTheme, ContentDirection, } from '@workday/canvas-kit-react/common';
export var useRTL = function (partialTheme) {
    var theme = useTheme(partialTheme);
    var shouldUseRTL = theme.canvas.direction === ContentDirection.RTL;
    return {
        shouldUseRTL: shouldUseRTL,
    };
};
