export function getLastPage(resultCount, totalCount) {
    return Math.ceil(totalCount / resultCount);
}
export function getRangeMin(range) {
    return range[0];
}
export function getRangeMax(range) {
    return range[range.length - 1];
}
export function getVisibleResultsMin(currentPage, resultCount) {
    return currentPage * resultCount - resultCount + 1;
}
export function getVisibleResultsMax(currentPage, resultCount, totalCount) {
    if (totalCount < currentPage * resultCount) {
        return totalCount;
    }
    return currentPage * resultCount;
}
