export function mergeCallback(callback1, callback2) {
    if (callback2) {
        return (function () {
            var args = [];
            for (var _i = 0; _i < arguments.length; _i++) {
                args[_i] = arguments[_i];
            }
            callback1.apply(null, args);
            callback2.apply(null, args);
        });
    }
    else {
        return callback1;
    }
}
