export var isFocusable = function (element) {
    var nodeName = element.nodeName.toLowerCase();
    var validInput = nodeName === 'input' && element.getAttribute('type') !== 'hidden';
    var validAnchor = nodeName === 'a' && element.hasAttribute('href');
    var validAudioVideo = ['audio', 'video'].includes(nodeName) && element.hasAttribute('controls');
    var validImgObject = ['img', 'object'].includes(nodeName) && element.hasAttribute('usemap');
    var validNativelyFocusable = [
        'button',
        'details',
        'embed',
        'iframe',
        'select',
        'textarea',
    ].includes(nodeName);
    var hasTabIndex = element.getAttribute('tabindex') === '0';
    return (validInput ||
        validAnchor ||
        validAudioVideo ||
        validImgObject ||
        validNativelyFocusable ||
        hasTabIndex);
};
export var getFirstFocusableElement = function (container) {
    var elements = container.querySelectorAll('*');
    for (var i = 0; i < elements.length; i++) {
        var element = elements.item(i);
        if (element && isFocusable(element)) {
            return element;
        }
    }
    return null;
};
export var getLastFocusableElement = function (container) {
    var elements = container.querySelectorAll('*');
    for (var i = elements.length - 1; i >= 0; i--) {
        var element = elements.item(i);
        if (element && isFocusable(element)) {
            return element;
        }
    }
    return null;
};
