import { createHook, useForkRef } from '@workday/canvas-kit-react/common';
export var usePopupTarget = createHook(function (_a, ref) {
    var events = _a.events, state = _a.state;
    var elementRef = useForkRef(ref, state.targetRef);
    return {
        ref: elementRef,
        onClick: function (event) {
            if (!state.targetRef.current) {
                state.targetRef.current = event.currentTarget;
            }
            if (state.visibility !== 'hidden') {
                events.hide({ event: event });
            }
            else {
                events.show({ event: event });
            }
        },
    };
});
